def main():
    if not os.path.exists(ICONS_DIR):
        os.makedirs(ICONS_DIR)
        
    for filename, content in ICONS.items():
        filepath = os.path.join(ICONS_DIR, filename)
        with open(filepath, "w") as f:
            f.write(content)
        print(f"Generated {filepath}")

if __name__ == "__main__":
    main()
